mapper.select:

			select boardNO, writer, title, regdate, replyCount, readCount 
			from (select rownum rn, boardNO, writer, title, regdate, replyCount, readCount
  			  from (select * from tbl_reportboard order by boardNO desc)
     		   where rownum <= #{pageNum} * #{amount})
			where rn > (#{pageNum} - 1) * #{amount}
